import React from 'react';
import classNames from 'classnames';
import { IFamilyExtNode } from 'relatives-tree';
import styles from './FamilyNode.module.css';
import Popup from "reactjs-popup";

interface Props {
  node: IFamilyExtNode;
  isRoot: boolean;
  onSubClick: (id: string) => void;
  style?: React.CSSProperties;
  namePerson: string;
}
function Iflink(link){
if (link){
    return(
<div>
hello
<a href={link}>See more</a>
</div>

)
}
}

export default React.memo<Props>(

  function FamilyNode({ node, isRoot, onSubClick, style, namePerson }) {
    const [show, setShow] = useState(false);

const handleClose = () => setShow(false);
const handleShow = () => setShow(true);
    return (
      <div className={styles.root} style={style}>
        <Popup trigger={
            <button
            className={classNames(
              styles.inner,
              styles[node.gender],
              isRoot && styles.isRoot,
            )}
          >
          <p className={styles.person}>
          {namePerson}
          </p>
          </button>}

        contentStyle = {{width:"200px", height:"300px",background: "#f2eecb", padding: "0px", border: "none"}}
        closeOnDocumentClick
        position="top center"
        on="hover"
        >

            {close => (
              <div>
              <div className = {styles.popupTop}>
                {node.name}
                <a className="close" onClick={close}>
                  &times;
                </a>
              </div>
                  <div className = {styles.popupContent}>
                  DOB: {node.dob}
                  DOD: {node.dod}
                  {Iflink(node.link)}
                  </div>

                <img src={node.src} />


              </div>
            )}

          </Popup>
        {node.hasSubTree && (
          <div
            className={classNames(styles.sub, styles[node.gender])}
            onClick={() => onSubClick(node.id)}
          />
        )}
      </div>
    );
  }
);
