import React, { Component } from 'react'
import LineTo from 'react-lineto';
//import data from '../familytree.json';
import { Collapse } from 'reactstrap';

export class Tree extends Component {

  constructor() {
    super();
    this.state = {
      showComponent1: false,
      showComponent2: false
    }
  }

  hideComponent = (name) => {
    switch (name) {
      case "showComponent1":
      this.setState({ showComponent1: !this.state.showComponent1 });
      break;
      case "showComponent2":
      this.setState({ showComponent2: !this.state.showComponent2 });
      break;
    }
  }

  render() {
    const {showComponent1, showComponent2} = this.state;

    return (
      <div id = "tree">
      {/*<h1>{this.state.data[0].text}React Connect Elements</h1>*/}
      <button onClick={() => this.hideComponent("showComponent1")}>
      toggle 1
      </button>
      <Collapse isOpen={this.state.showComponent1} timeout={{ enter: 100, exit: 100 }} >
      <div className="treecontainer">
      <div className="elements">
      <div className="elements-row">
      <div className="A" />
      <div className="B" />
      <LineTo from="A" to="B" borderColor='blue' borderWidth='40' zIndex = '10'/>
      </div>
      </div>
      </div>
      </Collapse>
      <button onClick={() => this.hideComponent("showComponent1")}>
      toggle 2
      </button>
      <Collapse isOpen={this.state.showComponent2} timeout={{ enter: 100, exit: 100 }} >
      <div className="treecontainer">
      <div className="elements">
      <div className="elements-row">
      <div className="C" />
      <div className="D" />
      <LineTo from="C" to="D" borderColor='blue' borderWidth='40' zIndex = '10'/>
      <LineTo from="C" to="B" borderColor='blue' borderWidth='40' zIndex = '10'/>
      </div>
      </div>
      </div>
      </Collapse>
      </div>

    )
  }
}
export default Tree
