import React, { Component } from 'react'
import ConnectElements from 'react-connect-elements';
import data from '../familytree.json';
import { Collapse } from 'reactstrap';

export class Tree extends Component {

  constructor() {
    super();
    this.state = {
      data: data.familytree,
      showComponent1: true,
      showComponent2: true
    }
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    switch (name) {
      case "showComponent1":
      this.setState({ showComponent1: !this.state.showComponent1 });
      break;
      case "showComponent2":
      this.setState({ showComponent2: !this.state.showComponent2 });
      break;
    }
  }
  onToggle(event) {this.setState({isOpenBol:!event})}

  render() {
    return (
      <div id = "tree">
      <h1>{this.state.data[0].text}React Connect Elements</h1>
      <button onClick={() => this.hideComponent("showComponent1")}>
      toggle
      </button>
      <Collapse isOpen={this.state.showComponent1} >
      <div className="treecontainer">
      <div className="elements">
      <div className="elements-row">
      <div className="element element1" />
      <div className="element element2" />
      </div>
      <div className="elements-row">
      <div className="element element4" />
      </div>
      </div>
      <ConnectElements
      selector=".elements"
      elements={[
        { from: '.element1', to: '.element4' },
        { from: '.element2', to: '.element4' },
      ]}
      />
      </div>
      </Collapse>
      <button onClick={() => this.hideComponent("showComponent2")}>
      toggle
      </button>
      <Collapse isOpen={this.state.showComponent2} >
      <div className="treecontainer">
      <div className="elements">
      <div className="elements-row">
      <div className="element" />
      <div className="element" />
      </div>
      <div className="elements-row">
      <div className="element element7" />
      </div>
      </div>
      <ConnectElements
      selector=".elements"
      elements={[
        { from: '.element3', to: '.element7' },
        { from: '.element5', to: '.element7' },
      ]}
      />
      </div>
      </Collapse>
      </div>
    )
  }
}
export default Tree
