import React from 'react';
import classNames from 'classnames';
import { IFamilyExtNode } from 'relatives-tree';
import styles from './FamilyNode.module.css';
import Popup from "reactjs-popup";

interface Props {
  node: IFamilyExtNode;
  isRoot: boolean;
  onSubClick: (id: string) => void;
  style?: React.CSSProperties;
  namePerson: string;
}

export default React.memo<Props>(
  function iflink(link){
    if(link){
      return(
    <div>
    hello
    </div>)
  }
},
  function FamilyNode({ node, isRoot, onSubClick, style, namePerson }) {
    return (
      <div className={styles.root} style={style}>
        <Popup trigger={
          <button
          className={classNames(
            styles.inner,
            styles[node.gender],
            isRoot && styles.isRoot,
          )}
        >
        <p className={styles.person}>
        {namePerson}
        </p>
        </button>} position="top left">

            {close => (
              <div className = {styles.popup}>
                {node.name}
                <button className="close" onClick={close}>
                  &times;
                </button>
                <img src={node.src} />
                DOB: {node.dob}
                DOD: {node.dod}
                iflink({node.link})


              </div>
            )}

          </Popup>
        {node.hasSubTree && (
          <div
            className={classNames(styles.sub, styles[node.gender])}
            onClick={() => onSubClick(node.id)}
          />
        )}
      </div>
    );
  }
);
