import React, { useState, useCallback } from 'react';
import PinchZoomPan from 'pinch-zoom-pan';
import { IFamilyNode, IFamilyExtNode } from 'relatives-tree';
import ReactFamilyTree from 'react-family-tree';
import FamilyNode from './FamilyNode/FamilyNode';

import nodes from '../familytree.json';
const myID = 'kuVISwh7w';

const WIDTH = 70;
const HEIGHT = 80;

export default React.memo<{}>(
  function Tree() {
    const [rootId, setRootId] = useState<string>(myID);
    const onResetClick = useCallback(() => setRootId(myID), []);

    return (
      <div className = "root">

      <div className="container">
          <div className="row" >
        <header>
          <h1>
            FamilyTree demo
          </h1>
          <h1>
            FamilyTree demo
          </h1>
          <h1>
            FamilyTree demo
          </h1>
          <h1>
            FamilyTree demo
          </h1>
          <h1>
            FamilyTree demo
          </h1>
          <a href="https://github.com/SanichKotikov/react-family-tree-example">GitHub</a>
        </header>
        </div>
            <div className="row" >
        <PinchZoomPan
          debug
          captureWheel
          min={0.5}
          max={2.5}
          className = "wrapper"
        >
          <ReactFamilyTree
            nodes={nodes as IFamilyNode[]}
            rootId={rootId}
            width={WIDTH}
            height={HEIGHT}
            renderNode={(node: IFamilyExtNode) => (
              <FamilyNode
                key={node.id}
                node={node}
                isRoot={node.id === rootId}
                onSubClick={setRootId}
                style={{
                  width: WIDTH,
                  height: HEIGHT,
                  transform: `translate(${node.left * (WIDTH / 2)}px, ${node.top * (HEIGHT / 2)}px)`,
                }}
              />
            )}
          />
        </PinchZoomPan>
        {rootId !== myID && (
          <div className = "reset" onClick={onResetClick}>
            Reset
          </div>
        )}
      </div>
      </div>
      </div>
    );
  }
);
