import React, { Component } from 'react'
import LineTo from 'react-lineto';
import { Collapse } from 'reactstrap';

class Tree2 extends Component{
  render(){return(
  <div id = "tree2">
  <div className="treecontainer">
  <div className="elements">
  <div className="elements-row">
  <div className="C" />
  <div className="D" />
  <LineTo from="C" to="D" borderColor='blue' borderWidth='40' zIndex = '10'/>
  <LineTo from="C" to="B" borderColor='blue' borderWidth='40' zIndex = '10'/>
  </div>
  </div>
  </div>

  </div>
)}
}

class Tree extends Component {

  constructor() {
    super();
    this.state = {
      showComponent1: false,
      showComponent2: false
    }
  }

  hideComponent = (name) => {
    switch (name) {
      case "showComponent1":
      this.setState({ showComponent1: !this.state.showComponent1 });
      break;
      case "showComponent2":
      this.setState({ showComponent2: !this.state.showComponent2 });
      break;
    }
  }
  display(){
  console.log(this.state.showComponent2)
  if(this.state.showComponent2){
    return(<Tree2 />)

  }
}
  render() {
    const {showComponent1, showComponent2} = this.state;

    return (
      <div id = "tree">
      {/*<h1>{this.state.data[0].text}React Connect Elements</h1>*/}
      <button onClick={() => this.hideComponent("showComponent1")}>
      toggle 1
      </button>
      <Collapse isOpen={showComponent1} timeout={{ enter: 100, exit: 100 }} >
      <div className="treecontainer">
      <div className="elements">
      <div className="elements-row">
      <div className="A" />
      <div className="B" />
      <LineTo from="A" to="B" borderColor='blue' borderWidth='40' zIndex = '10'/>
      </div>
      </div>
      </div>
      </Collapse>
      <button onClick={() => this.hideComponent("showComponent2")}>
      toggle 2
      </button>

      {/*<Collapse isOpen={showComponent2} timeout={{ enter: 100, exit: 100 }} >*/}
      {this.display()}
      </div>
    )
  }
}

export default Tree
